import {createSlice} from "@reduxjs/toolkit";

const userSlice = createSlice({
    name :'user',
    initialState  : {
        status : false,
        user : {}
    },
    redurcers : {
        login:(state) => {
            state.status = true
            //rest api den gelen veriye göre değiştirebilir. 
            state.user = {
                name : 'ibrahim',
                role :'customer',
                email : 'ibr@ibr.com',
                pass : '123456'
            }
        },
        register :(state,action) => {
            let {name,email,pass} = action.payload;
            state.status = true
            state.user = {
                name : name,
                role :'customer',
                email : email,
                pass : pass
            }
        },
        logout : (state) => {
            state.status = false
            state.user = {

            }
        }
    }
})

const userReducer = userSlice.reducer
export default userReducer
